mapper.update:
UPDATE BOARD_NOTICE
SET board_ttl = #{boardTtl},
    board_cntn = #{boardCntn}
WHERE board_no = #{boardNo}

UPDATE BOARD_NOTICE
SET is_del = 'Y'
WHERE board_no = #{boardNo}

UPDATE REPLY_NOTICE
SET reply_cntn = #{replyCntn}
WHERE reply_no = #{replyNo}

UPDATE REPLY_NOTICE
SET is_del = 'Y'
WHERE reply_no = #{replyNo}
